package com.javalec.base;

public class Bean_01 {
	// Field, Property
	private String name;
	private int value;
	// Constructor
	public Bean_01() {
		// TODO Auto-generated constructor stub
	}
	// Method
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getValue() {
		return value;
	}
	public void setValue(int value) {
		this.value = value;
	}
	
}
