package com.jsplec.bbs.dto;

import java.sql.Timestamp;

public class BDto {

	// Field

	int olSeq;
	String olName;
	String olTitle;
	Timestamp olDate;

	// Constructor

	public BDto() {
		// TODO Auto-generated constructor stub
	}

	public BDto(int olSeq, String olName, String olTitle, Timestamp olDate) {
		super();
		this.olSeq = olSeq;
		this.olName = olName;
		this.olTitle = olTitle;
		this.olDate = olDate;
	}

	public int getOlSeq() {
		return olSeq;
	}

	public void setOlSeq(int olSeq) {
		this.olSeq = olSeq;
	}

	public String getOlName() {
		return olName;
	}

	public void setOlName(String olName) {
		this.olName = olName;
	}

	public String getOlTitle() {
		return olTitle;
	}

	public void setOlTitle(String olTitle) {
		this.olTitle = olTitle;
	}

	public Timestamp getOlDate() {
		return olDate;
	}

	public void setOlDate(Timestamp olDate) {
		this.olDate = olDate;
	}

}
